                    handler.setEnabled(isEnabled);
                }
                else if(actionType.equals(ACTION_COMMAND))
                {
                    String commandDescriptor
                        = configService.getString(
                            actionPropName + ".commandDescriptor");

                    handler = new CommandNotificationHandlerImpl(
