		    line = line.substring(12, i-1);
		    table = line.trim();
		} else if (line.length() > 0) {
		    int i = line.indexOf(" ");
		    if (i > 0) {
			olds.add(table + " " + line.substring(0,i));
		    }
		}		    
	    }

	    PrintWriter out = new PrintWriter(args[2]);

	    // now go through new definition. Anything not in old is added.
