      return super.isModified()
          || (
          myModule instanceof DevKit
              ? myModuleDependenciesTab.isModified()
              : myEntriesEditor.isModified())
          || (
          myGenOut != null && !(myGenOut.getText().equals(getGenOutPath()))
      );
