  private Bln delete(final File file) throws QueryException {

    if(!file.exists()) {
      Err.or(input, QueryText.FILENOTEXISTS, file.getPath());
      return Bln.FALSE;
    }

    if(!file.canWrite()) {
      Err.or(input, QueryText.FILEDEL, file.getPath());
