  private File getExistingParent(final File file) throws QueryException {

    try {
      File f = file.getCanonicalFile();

      do {

        f = f.getParentFile();
        if(f == null) Err.or(input, QueryText.DIRINV, file);

      } while(!f.exists());
      return f;
