        File libraryFile;
        File librarySrcFile;

        File stdJarInDefaultPath = getFileInDir(jvmConfigurator.getJarName(), defaultPathToJarFile);
        if (stdJarInDefaultPath.exists()) {
            libraryFile = stdJarInDefaultPath;

