        //encoding name (mandatory)
        if(! tokenizer.hasMoreTokens())
            return null;
        String encoding = tokenizer.nextToken();

        //clock rate (mandatory)
        if(! tokenizer.hasMoreTokens())
            return null;
        int clockRate = Integer.parseInt(tokenizer.nextToken());

        //number of channels (optional)
        int nChans = 1;
        if(tokenizer.hasMoreTokens())
