        while ( version < masterVersion )
        {
            if ( slave.hasLog( version ) )
            {
                version++;
            }
            else
            {
                break;
            }
        }
        if ( version == masterVersion )
        {
            setStatus( Status.GET_MESSAGE );
            return true;
