          FontMetrics metrics = g.getFontMetrics();
          Shape savedClip = g.getClip();
          g.clipRect(r.x + 3, r.y + 1, r.width - 6, r.height - 3);
          g.drawString(myText, r.x + 7, r.y + (r.height + metrics.getAscent() - metrics.getDescent()) / 2 - 1);
          g.setClip(savedClip);
