    try {
      myNode = ComponentsUtil.nodeFromElement((Element) element.getChild(NODE).getChildren().get(0));

      Element scopeXML = element.getChild(SCOPE);
      String scopeType = scopeXML.getAttribute(SCOPE_TYPE).getValue();
