                if(window != null) {
                    window.dispose();
                    window = null;
                }
            }

            @Override
            public void popupMenuCanceled(PopupMenuEvent e) {
//                System.out.println("popupMenuCanceled");
                if(window != null) {
                    window.dispose();
                    window = null;
                }
            }
        };

        addMouseListener(new MouseAdapter() {
