		item.add(new Label("messageDate", ProfileUtils.convertDateToString(message.getDatePosted(), ProfileConstants.MESSAGE_DISPLAY_DATE_FORMAT)));

		//message body
		item.add(new Label("messageBody", new Model<String>(message.getMessage())));

        return item;
    }

