		}
		
		if (parameters!=null)  {
			retval.append("      ").append(XMLHandler.openTag("parameters"));
			
			retval.append("        ").append(XMLHandler.addTagValue("pass_all_parameters", passingAllParameters));
			
			for (int i=0;i<parameters.length;i++)
