			final byte[] bs = out.toString().getBytes(cs);
			response.setContentLength(bs.length);
			response.getOutputStream().write(bs);
		} catch (UiException ex) {
			log.error("Failed to process:\n"+content);
			throw ex;
		} finally {
			I18Ns.cleanup(request, old);
		}
	}

