                            } catch (IllegalStateException e)
                            {
                            } catch (OperationFailedException e)
                            {
                            }
                        }

                        timer = IDLE_TIMER;
                    }
                    lastPosition = currentPosition;
                }
                Thread.sleep(timer);
            } catch (InterruptedException e)
            {
            }
        } while (run && timer > 0);
