      deltaY = mouse.y - y;
    }

    final Dimension size = contents.getPreferredSize();
    final Rectangle rec = new Rectangle(new Point(x, y), size);
    ScreenUtil.moveRectangleToFitTheScreen(rec);

    if (rec.y < y) {
      rec.y += deltaY;
    }

    return rec.getLocation();
  }

