	    			return false;
	            }
	            
	            if (original_geometry.isEmpty() == rs.getBoolean("IS_CLOSED")) {
	            	LOG.info("Inconsistent area: " + rs.getBoolean("IS_CLOSED"));
	    			return false;
	            }
