	        			return false;
	        		}
	        	}
	        	
	    		LOG.info(node);	        		
	        }
	        return true;
	        
        } catch (ParseException e) {
        	LOG.error(e);
        	return false;
        } catch (PermissionDeniedException e) {
        	LOG.error(e);
        	return false;
		} finally {   
			if (rs != null)
				rs.close();
			if (ps != null)
				ps.close();	
