            else if(dist == minDistanceSq && minPrimitive != null
                    && ((n.id == 0 && n.isSelected())
                            || (!minPrimitive.isSelected() && (n.isSelected() || n.id == 0)))) {
                minPrimitive = n;
            }
        }
        return minPrimitive;
    }

