    public Collection<OsmPrimitive> getAllNearest(Point p) {
        Collection<OsmPrimitive> nearest = new HashSet<OsmPrimitive>();
        for (Way w : getCurrentDataSet().ways) {
            if (w.deleted || w.incomplete) {
