/**
     * Shows the Ingest dialog.
     */
    public void display() {
        Dimension screenDimension = Toolkit.getDefaultToolkit().getScreenSize();

        // set the popUp window / JFrame
        setSize(DIMENSIONS);
        int w = this.getSize().width;
        int h = this.getSize().height;

        // set the location of the popUp Window on the center of the screen
        setLocation((screenDimension.width - w) / 2, (screenDimension.height - h) / 2);

        // add the command to close the window to both buttons
        panel.setCloseButtonActionListener(new ActionListener() {
            @Override
            public void actionPerformed(ActionEvent e) {
                close();
            }
        });
        panel.setStartButtonActionListener(new ActionListener() {
            @Override
            public void actionPerformed(ActionEvent e) {
                close();
            }
        });

        add(panel);
        pack();
        setResizable(false);
        setVisible(true);
    }

modified_method:
/**
     * Shows the Ingest dialog.
     */
    public void display() {
        setLayout(new BorderLayout());
        Dimension screenDimension = Toolkit.getDefaultToolkit().getScreenSize();

        // set the popUp window / JFrame
        setSize(DIMENSIONS);
        int w = this.getSize().width;
        int h = this.getSize().height;

        // set the location of the popUp Window on the center of the screen
        setLocation((screenDimension.width - w) / 2, (screenDimension.height - h) / 2);

        add(panel, BorderLayout.PAGE_START);
        JButton startButton = new JButton("Start");
        JButton closeButton = new JButton("Close");
        startButton.addActionListener(new ActionListener() {

            @Override
            public void actionPerformed(ActionEvent e) {
                //pick the services
                List<IngestServiceAbstract> servicesToStart = panel.getServicesToStart();

                if (!servicesToStart.isEmpty()) {
                    manager.execute(servicesToStart, image);
                }

                //update ingest freq. refresh
                if (panel.freqSliderEnabled()) {
                    manager.setUpdateFrequency(panel.sliderValue());
                }
                close();
            }
        });
        closeButton.addActionListener(new ActionListener() {

            @Override
            public void actionPerformed(ActionEvent e) {
                close();
            }
        });
        add(startButton, BorderLayout.LINE_START);
        add(closeButton, BorderLayout.LINE_END);

        pack();
        setResizable(false);
        setVisible(true);
    }

