        if (project == null)
        {
            presentation.setEnabled(false);
            return;
        }

        VirtualFile[] files = (VirtualFile[])context.getData(DataConstants.VIRTUAL_FILE_ARRAY);
        Editor editor = (Editor)context.getData(DataConstants.EDITOR);
