public MultiPartitionParticipantTxnState(Mailbox mbox, ExecutionSite site,
                                             TransactionInfoBaseMessage notice)
    {
        super(mbox, site, notice);
        m_hsId = site.getSiteId();
        m_nonCoordinatingSites = null;
        m_isCoordinator = false;

