@Override
		public String toRuleString(boolean avoidMonths) {
			StringBuilder b = new StringBuilder(25);
			// Month
			boolean dash  = false;
			boolean first = true;
			if (!avoidMonths) {
				for (int i = 0; i < 12; i++) {
					if (months[i]) {
						if (i > 0 && months[i - 1] && i < 11 && months[i + 1]) {
							if (!dash) {
								dash = true;
								b.append("-"); //$NON-NLS-1$
							}
							continue;
						}
						if (first) {
							first = false;
						} else if (!dash) {
							b.append(", "); //$NON-NLS-1$
						}
						b.append(monthsStr[i]);
						dash = false;
					}
				}
				if (b.length() != 0) {
					b.append(": ");
				}
			}
			// Day
			boolean open24_7 = true;
			for (int i = 0; i < 7; i++) {
				if (!days[i]) {
					open24_7 = false;
					break;
				}
			}
			appendDaysString(b);
			// Time
			if (startTimes == null || startTimes.length == 0){
				b.append(" off ");
			} else {
				for (int i = 0; i<startTimes.length; i++) {
					int startTime = startTimes[i];
					int endTime = endTimes[i];
					if (open24_7 && startTime == 0 && endTime / 60 == 24) {
						return "24/7";
					}
					b.append(" "); //$NON-NLS-1$
					int stHour = startTime / 60;
					int stTime = startTime - stHour * 60;
					int enHour = endTime / 60;
					int enTime = endTime - enHour * 60;
					formatTime(stHour, stTime, b);
					b.append("-"); //$NON-NLS-1$
					formatTime(enHour, enTime, b);
					b.append(",");
				}
			}
			return b.substring(0, b.length()-1);
		}

