                }
                else {
                    if (ItemState.DROPPED.toString().equals(replicationResponse.getStatus())) {
                        response.setStatus(404);
                    } else {
                        response.setStatus(400);
                    }
                }

                response.getWriter().append(replicationResponse.toString());
            } catch (ReplicationAgentException e) {
