   */
  private NSArray quoteArrayContents (NSArray<String> array) {
    NSMutableArray<String> result = new NSMutableArray<String>(array.count());
    for (String identifier : array) {
      String quotedString = this.quoteIdentifier(identifier);
      result.add(quotedString);
