                    } catch (TskCoreException ex) {
                        logger.log(Level.SEVERE, "Error adding installed program artifact to blackboard."); //NON-NLS                                    
                    }
                } else if (dataType.equals("Profiler")) { // NON-NLS

                    String os = "";
                    String procArch = "";
                    String procId = "";
                    String tempDir = "";

                    for (int j = 0; j < myartlist.getLength(); j++) {
