      bodySb.i().p("if (Float.isNaN(preds[1])) throw new RuntimeException(\"Predicted regression target NaN!\");").nl();
    }

    fileCtxSb.p(model);
    toJavaUnifyPreds(bodySb);
    toJavaFillPreds0(bodySb);
  }

