public void start() {
        for (AckReader reader : m_ackReaders) {
            reader.start();
        }
    }

modified_method:
public void start() {
        log().info("start: Starting "+m_ackReaders.size()+" readers...");
        for (AckReader reader : m_ackReaders) {
            log().debug("start: Starting reader: "+reader);
            reader.start();
        }
