        connectVertices(
                vmwareManagementServer + "/" + vmwareManagedObjectId + "->" + vmwareManagementServer + "/" + vmwareHostSystemId,
                virtualMachineVertex,
                getVertex(getVertexNamespace(), vmwareManagementServer + "/" + vmwareHostSystemId),
                getEdgeNamespace()
        );
