 private String extractSearchEngineQuery(String url){
      String x = "NoQuery";
      SearchEngine eng = getSearchEngine(url);
        for(Map.Entry<String,String> kvp : eng.getSplits()){
            if(url.contains(kvp.getKey())){
                x = split2(url, kvp.getValue());
                break;
