      } else if (tagName.equals("p") && posType.equals("relative")
                 && word.equalsIgnoreCase("que")) {
        return "pr0cn000";
      }

      if (simplifiedTagset) {
        // If we are using the simplfied tagset, we can make some more
        // broad inferences
        if (tagName.equals("a")) {
          return "aq0000";
        } else if (posType.equals("proper")) {
          return "np00000";
        } else if (posType.equals("common")) {
          return "nc0s000";
        }
      }

      if (node.hasAttribute(ATTR_PUNCT)) {
        return "f";
      }
    }

    return pos;
  }

