			long millis=stop.getTime()-start.getTime();
			log.logMinimal("Pan", "Processing ended after "+(millis/1000)+" seconds.");
			if (trans.getResult().getNrErrors()==0) 
			{
				trans.printStats((int)millis/1000);
                System.exit(0);
