protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException
    {
        if (!request.getContextPath().equals(CONTEXT_PATH)) return;
        
        if (log.isDebug()) log.logDebug(toString(), Messages.getString("StartJobServlet.Log.StartJobRequested"));

        String jobName = request.getParameter("name");
        boolean useXML = "Y".equalsIgnoreCase( request.getParameter("xml") );

        response.setStatus(HttpServletResponse.SC_OK);
        
        PrintWriter out = response.getWriter();
        if (useXML)
        {
            response.setContentType("text/xml");
            response.setCharacterEncoding(Const.XML_ENCODING);
            out.print(XMLHandler.getXMLHeader(Const.XML_ENCODING));
        }
        else
        {
            response.setContentType("text/html");
            out.println("<HTML>");
            out.println("<HEAD>");
            out.println("<TITLE>Start job</TITLE>");
            out.println("<META http-equiv=\"Refresh\" content=\"2;url=/kettle/jobStatus?name="+jobName+"\">");
            out.println("</HEAD>");
            out.println("<BODY>");
        }
    
        try
        {
            Job job = jobMap.getJob(jobName);
            if (job!=null)
            {
            	// First see if this job already ran to completion.
            	// If so, we get an exception is we try to start() the job thread
            	//
            	if (job.isInitialized() && !job.isActive())
            	{
            		// Re-create the job from the jobMeta
            		//
            		// We might need to re-connect to the database
            		//
            		if (!job.getRep().getRepositoryInfo().isLocked())
            		{
            			job.getRep().connect(toString());
            		}

            		job = new Job(LogWriter.getInstance(), StepLoader.getInstance(), job.getRep(), job.getJobMeta());
            	}
            	
                // Log to a String & save appender for re-use later.
                Log4jStringAppender appender = LogWriter.createStringAppender();
                log.addAppender(appender);
                jobMap.addAppender(jobName, appender);
                
                job.start(); // runs the thread in the background...

                String message = Messages.getString("StartJobServlet.Log.TransStarted",jobName);
                if (useXML)
                {
                    out.println(new WebResult(WebResult.STRING_OK, message).getXML());
                }
                else
                {
                    
                    out.println("<H1>"+message+"</H1>");
                    out.println("<a href=\"/kettle/jobStatus?name="+jobName+"\">" +Messages.getString("JobStatusServlet.BackToJobStatusPage") +"</a><p>");
                    
                    
                }
            }
            else
            {
                String message = Messages.getString("StartJobServlet.Log.SpecifiedJobNotFound",jobName);
                if (useXML)
                {
                    out.println(new WebResult(WebResult.STRING_ERROR, message));
                }
                else
                {
                    out.println("<H1>"+message+"</H1>");
                    out.println("<a href=\"/kettle/status\">" + Messages.getString("TransStatusServlet.BackToStatusPage") + "</a><p>");
                }
            }
        }
        catch (Exception ex)
        {
            if (useXML)
            {
                out.println(new WebResult(WebResult.STRING_ERROR, "Unexpected error during job start:"+Const.CR+Const.getStackTracker(ex)));
            }
            else
            {
                out.println("<p>");
                out.println("<pre>");
                ex.printStackTrace(out);
                out.println("</pre>");
            }
        }

        if (!useXML)
        {
            out.println("<p>");
            out.println("</BODY>");
            out.println("</HTML>");
        }
    }

