								final String userName = Accounts.lookupUserById(userId).getName();
								final String accountName = Accounts.lookupAccountById(accountId).getName();

								QueueSender queueSender = QueueFactory.getInstance().getSender(QueueIdentifier.S3);

								queueSender.send(new S3Event(true, size / WalrusProperties.M,
										userId, userName, accountId, accountName));

							} catch(Exception ex) {
								//no delete marker found.
								LOG.error("Deletion failed for: " + bucketName + "/" + objectKey, ex);
							}
							dbObject.commit();

							if (logData != null) {
								logData.setTurnAroundTime(Long
										.parseLong(new String(dataMessage
												.getPayload())));
							}
							// restart all interrupted puts
							WalrusMonitor monitor = messenger.getMonitor(key);
							synchronized (monitor) {
								monitor.setLastModified(lastModified);
								monitor.setMd5(md5);
								monitor.notifyAll();
							}
							//messenger.removeMonitor(key);
							messenger.clearQueues(key);
							messenger.removeQueue(key, randomKey);
							LOG.info("Transfer complete: " + key);


							break;
						} else {
							assert (WalrusDataMessage.isData(dataMessage));
							byte[] data = dataMessage.getPayload();
							// start writing object (but do not commit yet)
							try {
								if (fileIO != null)
									fileIO.write(data);
							} catch (IOException ex) {
								LOG.error(ex);
							}
							// calculate md5 on the fly
							size += data.length;
							if (digest != null) {
								digest.update(data);
							}
						}
					}
				} catch (InterruptedException ex) {
					LOG.error(ex, ex);
					messenger.removeQueue(key, randomKey);
					throw new EucalyptusCloudException("Transfer interrupted: " + key + "." + randomKey);
				}
			} else {
				db.rollback();
				messenger.removeQueue(key, randomKey);
				throw new AccessDeniedException("Bucket", bucketName, logData);
			}
		} else {
			db.rollback();
			messenger.removeQueue(key, randomKey);
			throw new NoSuchBucketException(bucketName);
		}
		reply.setEtag(md5);
		reply.setLastModified(DateUtils.format(lastModified.getTime(),
				DateUtils.ISO8601_DATETIME_PATTERN)
				+ ".000Z");
		return reply;
	}

