            stream.writeUTF( entry.getKey() );
            stream.writeUTF( entry.getValue() );
        }

        for ( NodeInstance nodeInstance : processInstance.getNodeInstances() ) {
            stream.writeShort( PersisterEnums.NODE_INSTANCE );
            writeNodeInstance( context,
