        {
            MavenProject givenProject = getMavenProject( artifact );
            if ( givenProject != null )
            {
                File outFile = new File( outputDirectory, artifact.getDoapFileName() );
                writeDoapFile( givenProject, outFile );
                return;
            }
        }

        // current project
