private void parseTemplateText(Node parent) {

        if (!reader.hasMoreInput())
            return;

        CharArrayWriter ttext = new CharArrayWriter();

        while (reader.hasMoreInput()) {
            int ch = reader.nextChar();
            if (ch == '<') {
                // Check for "<\%"
                if (reader.nextChar() == '\\') {
                    if (reader.nextChar() == '%') {
                        ttext.append('<');
                        ttext.append('%');
                    } else {
                        reader.pushChar();
                        reader.pushChar();
                        if (ttext.size() == 0) {
                            ttext.append('<');
                        } else {
                            reader.pushChar();
                            break;
                        }
                    }
                } else {
                    reader.pushChar();
                    if (ttext.size() == 0) {
                        ttext.append('<');
                    } else {
                        reader.pushChar();
                        break;
                    }
                }
            } else if (ch == '\\' && !pageInfo.isELIgnored()) {
                int next = reader.nextChar();
                if (next == '$' || next == '#') {
                    if (reader.nextChar() == '{') {
                        ttext.write(next);
                        ttext.append('{');
                    } else {
                        ttext.append('\\');
                        ttext.write(next);
                        reader.pushChar();
                    }
                } else {
                    ttext.append('\\');
                    reader.pushChar();
                }
            } else if ((ch == '$' || ch == '#' && !pageInfo.isDeferredSyntaxAllowedAsLiteral()) &&
                    !pageInfo.isELIgnored()) {
                if (reader.nextChar() == '{') {
                    reader.pushChar();
                    reader.pushChar();
                    break;
                } else {
                    reader.pushChar();
                    ttext.write(ch);
                }
            } else {
                ttext.write(ch);
            }
        }

        @SuppressWarnings("unused")
        Node unused = new Node.TemplateText(ttext.toString(), start, parent);
    }

