			for (int i = 0; i < jsonArray.length(); i ++) {
				JSONObject jsonObject = jsonArray.getJSONObject(i);

				if (jsonObject.has(recentLayoutBranchIdKey)) {
					return GetterUtil.getLong(jsonObject.getString(
						recentLayoutBranchIdKey));
				}
			}
		}
