      builder.error(GroovyBundle.message("lcurly.expected"));
      cbMarker.rollbackTo();
      return false;
    }

    ParserUtils.getToken(builder, mNLS);

    PsiBuilder.Marker constructorInvokationMarker = builder.mark();
    if (parseExplicitConstructor(builder)) {
