            pstmt.executeUpdate();
            pstmt.close();
        }
        long mgmtNicId =
            insertNic(conn, managementNetworkId, cpId, running, privateMac, privateIp, privateNetmask, "Start", podGateway, null, "PodBasedNetworkGuru", false, 1,
                "Static", privateIp != null ? (cpId + privateIp) : null);
        if (privateIp != null) {
