public void addModifiedNotSavedChange(final VirtualFile file) throws SVNException {
    final FilePath filePath = new FilePathImpl(file);
    final Info svnInfo = myVcs.getInfo(file);

    if (svnInfo != null) {
      final Status svnStatus = new Status();
