public void run() {
		freenet.support.Logger.OSThread.logPID(this);
		try {
			realRun();
		//The last thing that realRun() does is register as a request-sender listener, so any exception here is the end.
		} catch(NotConnectedException e) {
			Logger.normal(this, "requestor gone, could not start request handler wait");
			node.removeTransferringRequestHandler(uid);
			tag.handlerThrew(e);
			boolean dontUnlock;
			synchronized(this) {
				dontUnlock = this.dontUnlock;
			}
			if(!dontUnlock)
				tag.unlockHandler();
		} catch(Throwable t) {
			Logger.error(this, "Caught " + t, t);
			node.removeTransferringRequestHandler(uid);
			tag.handlerThrew(t);
			boolean dontUnlock;
			synchronized(this) {
				dontUnlock = this.dontUnlock;
			}
			if(!dontUnlock)
				tag.unlockHandler();
		}
	}

