private static void doTestCoalescePairNotNull(Client cl, String col1, String col2) throws Exception {
        // coalesce(col2, col1) == col2
        String sql = "SELECT DECODE(COALESCE("+col2+","+ col1+")," + col2 + ", 0, 1) "
                + "FROM C_NULL WHERE ID = 2;";
