    return new AbstractIterator<MetricsScanResult>() {
      @Override
      protected MetricsScanResult computeNext() {
        Row rowResult;
        while ((rowResult = scanner.next()) != null) {
          rowScanned++;
          byte[] rowKey = rowResult.getRow();
          Map<byte[], byte[]> columnValue = rowResult.getColumns();

