            if (game.getCombat().attacksAlone()) {
                TargetCreaturePermanent target = new TargetCreaturePermanent();
                this.addTarget(target);
                this.getTargets().get(0).add(game.getCombat().getAttackers().get(0),game);
                return true;
            }
        }
        return false;
    }

