                    //LAUNCHER_TYPE
                    m_statementResourceUpdate.setInt(8,file.getLauncherType());
                    //LAUNCHER_CLASSNAME
                    m_statementResourceUpdate.setString(9,file.getLauncherClassname());
                    //DATE_LASTMODIFIED
                    m_statementResourceUpdate.setLong(10,System.currentTimeMillis());
