     }
        Map<String, Object> model  = m_inventoryService.getBuckets(nodeid,request.isUserInRole(Authentication.ADMIN_ROLE));
        ModelAndView modelAndView = new ModelAndView("admin/storage/storageAdmin","model",model);
        return modelAndView;
    }

