            if (m == null) {
                throw new MapNotFoundException("Maps with name "
                        + mapName + " don't exist.");
            }
            retVMap = new VMap[m.length];
            for (int i = 0; i < m.length; i++) {
                retVMap[i] = getMap(m[i].getId());
