          wrapper.put(new Pair<Integer, List<Integer>>(tCount, docIndices));

          while (wrapper.peek()) {
            Pair<Integer, Double> result = wrapper.poll();
            int doneTCount = result.first();
            double batchProb = result.second();
            prob += batchProb;
            combine2DArr(E, batchE[doneTCount]);
          }
          index = endIndex;
        }
        wrapper.join();
        while (wrapper.peek()) {
          Pair<Integer, Double> result = wrapper.poll();
          int doneTCount = result.first();
          double batchProb = result.second();
          prob += batchProb;
          combine2DArr(E, batchE[doneTCount]);
        }
      }
    } else {
